mapper.select:
select count(*)
from HEART
where mem_id = #{mem_id}

select count(*)
from MYPLAN
where mem_id = #{mem_id}

mapper.update:
update member 
set  e_mail = #{e_mail}, tel = #{tel}, mem_img = #{mem_img}
where mem_id = #{mem_id}

update member 
set  mem_pw = #{mem_pw}
where mem_id = #{mem_id}

mapper.select:
select e_mail,tel,mem_img,mem_id 
from member
where mem_id = #{mem_id}

select mem_img
from member
where mem_id = #{mem_id}
