mapper.select:
select count(*)
from heart
where mem_id = #{mem_id}

select count(*)
from myplan
where mem_id = #{mem_id}

mapper.update:
update member 
set  e_mail = #{e_mail}, tel = #{tel}, mem_img = #{mem_img}
where mem_id = #{mem_id}

update member 
set  mem_pw = #{mem_pw}
where mem_id = #{mem_id}

mapper.select:
select e_mail,tel,mem_img,mem_id 
from member
where mem_id = #{mem_id}

select mem_img
from member
where mem_id = #{mem_id}

select product_id
from plan_content
where plan_id = #{plan_id}

select count(*)
from heart
where mem_id = #{mem_id}
and heart_id = #{product_id}

mapper.delete:
delete from heart
where mem_id = #{mem_id}
and heart_id = #{heart_id}

mapper.select:
select count(*)
from heart
where heart_id = #{product_id}
