from review
where mem_id = #{mem_id}

mapper.update:
update member 
set  e_mail = #{e_mail}, tel = #{tel}, mem_img = #{mem_img}
where mem_id = #{mem_id}

update member 
set  mem_pw = #{mem_pw}
where mem_id = #{mem_id}

mapper.select:
